insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50173, 'What is Authoritative Source?', '<p><span data-sheets-userformat=''{"2":2621,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"12":0,"14":{"1":2,"2":16711680}}'' data-sheets-value=''{"1":2,"2":"Authoritative Source represents object is accurately matching with Business Glossary definition. If the customer thinks it is closely matched, then they will set the object as an Authoritative Source. We can set/ unset Authoritative Source from the Business Glossary dictionary page."}'' style="font-size: 10pt; font-family: Calibri, Arial; font-style: normal; color: rgb(0, 0, 0); position: static;">Authoritative Source represents object is accurately matching with Business Glossary definition. If the customer thinks it is closely matched, then they will set the object as an Authoritative Source. We can set/ unset Authoritative Source from the Business Glossary dictionary page.</span> </p>', 'sq.authsource', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50174, 'What happens when you enable the significant data change notification?
', '<p><span data-sheets-userformat=''{"2":2621,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":[null,2,0]},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"12":0,"14":[null,2,16711680]}'' data-sheets-value=''{"1":2,"2":"Select the Notify significant metadata and data changes and enter the percentage. A notification message is sent when the metadata and data changes exceed the entered percentage limit."}'' style="font-size: 10pt; font-family: Calibri, Arial; font-style: normal; color: rgb(0, 0, 0); position: static;">Select the Notify significant metadata and data changes and enter the percentage. A notification message is sent when the metadata and data changes exceed the entered percentage limit.</span> </p>', 'sq.datachangenotify', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50175, 'What is an Auxiliary Term?', '<p><span data-sheets-userformat=''{"2":15357,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"9":0,"10":0,"11":0,"12":0,"14":{"1":2,"2":16711680},"15":"Arial","16":10}'' data-sheets-value=''{"1":2,"2":"An auxiliary term is created for more terms available to occupied by the customer according to their organisation needs. A term which is created in an auxiliary domain is an auxiliary term. For auxiliary terms, we cannot perform data associations like copy title to catalog, Copy business description to catalog. Any data object can have 0 or 1 primary terms and any number of auxiliary terms. For example, a Table cannot have more than 1 primary terms and can have any number of auxiliary terms. \n"}'' style="font-size: 14px; font-family: Roboto, sans-serif; font-weight: normal; font-style: normal; color: rgb(0, 0, 0); position: static;">An auxiliary term is created for more terms available to occupied by the customer according to their organisation needs. A term which is created in an auxiliary domain is an auxiliary term. For auxiliary terms, we cannot perform data associations like copy title to catalog, Copy business description to catalog. Any data object can have 0 or 1 primary terms and any number of auxiliary terms. For example, a Table cannot have more than 1 primary terms and can have any number of auxiliary terms. <br style="position: static;"></span> </p>', 'sq.auxterm', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50176, 'What are RACI User Roles in OvalEdge?', '<p><span data-sheets-userformat=''{"2":3069,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"9":0,"10":0,"11":0,"12":0,"14":{"1":2,"2":16711680}}'' data-sheets-value=''{"1":2,"2":"RACI tab is used to assign one or multiple user roles to analyze, validate,approve or reject the term. Once user roles are assigned, then the user selects the &#8216;Initiate Workflow&#8217; button to complete the approval process and publish the term. The Business Glossary- Term is enhanced with a new RACI feature to add multiple users under different predefined (R- Responsible, A- Accountable, C- Consulted, I-Informed) Roles to publish the term with the RACI assigned users."}'' style="font-size: 14px; font-family: Roboto, sans-serif; font-style: normal; color: rgb(0, 0, 0); position: static;">RACI tab is used to assign one or multiple user roles to analyze, validate,approve or reject the term. Once user roles are assigned, then the user selects the &lsquo;Initiate Workflow&rsquo; button to complete the approval process and publish the term. The Business Glossary- Term is enhanced with a new RACI feature to add multiple users under different predefined (R- Responsible, A- Accountable, C- Consulted, I-Informed) Roles to publish the term with the RACI assigned users.</span></p>', 'sq.raci', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50177, 'What is a Team Message?', '<p><span data-sheets-userformat=''{"2":3069,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"9":0,"10":0,"11":0,"12":0,"14":{"1":2,"2":16711680}}'' data-sheets-value=''{"1":2,"2":"In the My Profile page, a new feature called Team messages  allows users to first create a team of members,  then we can add those teams as owner or steward for any object.  In the Team messages, users are invited into one or more teams.  Users can collaborate with individuals or teams.\r"}'' style="font-size: 14px; font-family: Roboto, sans-serif; font-style: normal; color: rgb(0, 0, 0); position: static;">In the My Profile page, a new feature called Team messages &nbsp;allows users to first create a team of members, &nbsp;then we can add those teams as owner or steward for any object. &nbsp;In the Team messages, users are invited into one or more teams. &nbsp;Users can collaborate with individuals or teams. </span> </p>', 'sq.msgteam', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50178, 'What are Remote Audit Trails? ', '<div style="position: static;"><span style="font-size: 10.5pt; font-family: Arial; color: rgb(65, 65, 65); background-color: rgb(255, 255, 255); font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">Remote Audit Trail helps a user to understand the history of activities done when OvalEdge master with remote connection. A user may view the remote audit trails details for the action taken on the Remote users, Remote Roles, and privileges of schema and tables along with policies(based on the Connector) which gets updated in the Audit tables. This action may be Add,Delete, or Update are recorded in the remote audit trails.</span></div><div style="position: static;"><br style="position: static;"></div>', 'sq.remotetrails', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50179, 'What is an Auxiliary Domain?', '<p dir="ltr" style="line-height: 1.38; margin-top: 12pt; margin-bottom: 12pt; position: static;"><span style="font-size: 14px; font-family: Roboto, sans-serif; color: rgb(0, 0, 0); background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap; position: static;">In OvalEdge, at the time of creating a new domain, an admin user is provided with the option to create an Auxiliary domain. Primary domain will have primary terms and similarly, an auxiliary domain will have one or many auxiliary terms created. At the time of associating the terms to any of the objects(Schema, Database, Table, Table Column, File, File Columns), the primary term can be mapped to &nbsp;objects that belongs primary domain and whereas one or more auxiliary terms can be assigned to the auxiliary domain</span></p>', 'sq.auxdomain', true, '', '', 'admin', now());


