insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50182, 'What is OvalEdge API tool in Advanced Tools?', '<p><span data-sheets-userformat=''{"2":3069,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"9":0,"10":0,"11":0,"12":0,"14":{"1":2,"2":16711680}}'' data-sheets-value=''{"1":2,"2":"OvalEdge is a data catalog which creates a comprehensive compilation of all the data sources in an organization for better access and analysis of data. OvalEdge provides role-based access to tiered users of the application supporting a variety of authentication mechanisms from usernames and passwords to more complicated Active Directory or OKTA security measures. For OKTA, OvalEdge uses SAML protocol for Single Sign On into the application. You can use OKTA for authentication and add an additional authorization feature.\r"}'' style="font-size: 14px; font-family: Roboto, sans-serif; font-style: normal; color: rgb(0, 0, 0); position: static;">OvalEdge is a data catalog which creates a comprehensive compilation of all the data sources in an organization for better access and analysis of data. OvalEdge provides role-based access to tiered users of the application supporting a variety of authentication mechanisms from usernames and passwords to more complicated Active Directory or OKTA security measures. For OKTA, OvalEdge uses SAML protocol for Single Sign On into the application. You can use OKTA for authentication and add an additional authorization feature. This tool allows access to OvalEdge API</span></p><p><span style="font-family: Roboto,sans-serif;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><br></span></span></span></p><p><span style="font-family: Roboto,sans-serif;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span data-sheets-userformat=''{"2":3069,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"9":0,"10":0,"11":0,"12":0,"14":{"1":2,"2":16711680}}'' data-sheets-value=''{"1":2,"2":"OvalEdge is a data catalog which creates a comprehensive compilation of all the data sources in an organization for better access and analysis of data. OvalEdge provides role-based access to tiered users of the application supporting a variety of authentication mechanisms from usernames and passwords to more complicated Active Directory or OKTA security measures. For OKTA, OvalEdge uses SAML protocol for Single Sign On into the application. You can use OKTA for authentication and add an additional authorization feature.\r"}'' style="font-style: normal; position: static;">Steps to access OvalEdge API&nbsp;</span></span></span></span></p><p><span style="font-family: Roboto,sans-serif;"><span style="color: rgb(0, 0, 0);"><span style="font-size: 14px;"><span data-sheets-userformat=''{"2":3069,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"9":0,"10":0,"11":0,"12":0,"14":{"1":2,"2":16711680}}'' data-sheets-value=''{"1":2,"2":"OvalEdge is a data catalog which creates a comprehensive compilation of all the data sources in an organization for better access and analysis of data. OvalEdge provides role-based access to tiered users of the application supporting a variety of authentication mechanisms from usernames and passwords to more complicated Active Directory or OKTA security measures. For OKTA, OvalEdge uses SAML protocol for Single Sign On into the application. You can use OKTA for authentication and add an additional authorization feature.\r"}'' style="font-style: normal; position: static;">1. Click on Advanced Tools, advanced tools page is displayed.</span></span></span></span></p><p><span data-sheets-userformat=''{"2":3069,"3":{"1":0},"5":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"6":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"7":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"8":{"1":[{"1":2,"2":0,"5":{"1":2,"2":0}},{"1":0,"2":0,"3":3},{"1":1,"2":0,"4":1}]},"9":0,"10":0,"11":0,"12":0,"14":{"1":2,"2":16711680}}'' data-sheets-value=''{"1":2,"2":"OvalEdge is a data catalog which creates a comprehensive compilation of all the data sources in an organization for better access and analysis of data. OvalEdge provides role-based access to tiered users of the application supporting a variety of authentication mechanisms from usernames and passwords to more complicated Active Directory or OKTA security measures. For OKTA, OvalEdge uses SAML protocol for Single Sign On into the application. You can use OKTA for authentication and add an additional authorization feature.\r"}'' style="font-size: 14px; font-family: Roboto, sans-serif; font-style: normal; color: rgb(0, 0, 0); position: static;">2. Click on the OvalEdge API&rsquo;s. </span></p>', 'sq.apitool', true, '', '', 'admin', now());


insert into helpcenter_topic(topicid, description, content, context, active, type, external_link, createdby, createdate) values (50183, 'What is Temp Lineage Correction option in Advanced Tools?', '<p><span style="font-family: Roboto, sans-serif; font-size: 14px; color: rgb(0, 0, 0);">We have lots of connectors which allow users to create Auto build lineage and also build lineage from the scripts. When we crawl connections and build lineage, we might not find the respective tables/columns in our application to get them associated. In this case, the system will create temporary schemas, tables and columns and build lineage using them. When we crawl original schemas and tables, the temp tables should be replaced with these original tables. We can use the OvalEdge - &#39;Temp lineage correction&#39; to achieve it.</span></p><p><br></p><p><br></p><p><br></p>', 'sq.temptool', true, '', '', 'admin', now());


