		'Alert Audit Log Report ',
		'OvalEdge Reports',
		-1,
	'SELECT Alertname, 
       Message,
       Datasetname, 
       Count(Distinct alert.alertid) Alerts 
FROM   alert 
       INNER JOIN alertuser 
               ON ( alert.alertid = alertuser.alertid ) 
       INNER JOIN dataset 
               ON ( alert.datasetid = dataset.datasetid ) 
       INNER JOIN message 
               ON ( alertuser.userid = message.hashtagoruserid ) 

GROUP  BY alertname, 
          message, 
          datasetname',
	'SELECT Distinct Alertname, 
       Message, 
       Helptext, 
       Datasetname, 
      DATE_FORMAT(message.LASTMODDATE, "%Y-%m-%d %h:%i %p") timeraised, 
       alertuser.userid     Alertuser 
FROM   alert 
       INNER JOIN alertuser 
               ON ( alert.alertid = alertuser.alertid ) 
       INNER JOIN dataset 
               ON ( alert.datasetid = dataset.datasetid ) 
       INNER JOIN message 
               ON ( alertuser.userid = message.hashtagoruserid )
       INNER JOIN comment
              ON (comment.commentid = message.commentid and alert.datasetid = comment.objectid) 
where comment.commenttype = "SYSTEM_ALERT" and comment.objecttype is null 
and Cast(Cast(message.LASTMODDATE  AS DATE)as char)=?',
